graphql.query:
{
  getAssets {
    _id
    key
    assetBasicInfo {
        name
        type
    }
    assetCustom {
        model
        manufacturer
    }
    resourceGroup {
        assetKey
    }
  }
}

{
  getGraphqlAssetsPag (
      pagination: {
            page: 0
            limit: 10
            operation: "FIRST"
        }){
        total
        pagination {
            cursor
        }
        items {
        _id
        key
        assetBasicInfo {
            name
            type
        }
        assetCustom {
            model
            manufacturer
        }
        resourceGroup {
            assetKey
        }
    }
  }  
}

